// ğŸš€ å‹•æ…‹æº–å‚™æŠ•å½±ç‰‡è¤‡è£½è³‡æ–™ - æ”¯æ´ä»»ä½•æ¬„ä½çµæ§‹

const webhookData = $('Webhook Trigger').first().json.body;
const slides = webhookData.slides;
const presentation = $input.first().json;
const presentationId = presentation.presentationId;

// å–å¾—æ¨¡æ¿æŠ•å½±ç‰‡çµæ§‹
const templateSlide = presentation.slides && presentation.slides[0] ? presentation.slides[0] : null;

if (!templateSlide) {
  throw new Error('æ‰¾ä¸åˆ°æ¨¡æ¿æŠ•å½±ç‰‡');
}

// ğŸ” å‹•æ…‹åˆ†ææ¨¡æ¿ä¸­çš„æ‰€æœ‰ placeholder
// æƒææ‰€æœ‰æ–‡å­—å…ƒç´ æ‰¾å‡º {{}} æ ¼å¼çš„ placeholder
const textPlaceholders = new Set();
const imagePlaceholders = new Set();

// æƒææ–‡å­—å…ƒç´ 
if (templateSlide.pageElements) {
  templateSlide.pageElements.forEach(element => {
    // è™•ç†å½¢ç‹€ä¸­çš„æ–‡å­—
    if (element.shape && element.shape.text && element.shape.text.textElements) {
      element.shape.text.textElements.forEach(textElement => {
        if (textElement.textRun && textElement.textRun.content) {
          const content = textElement.textRun.content;
          const matches = content.match(/{{\s*([A-Za-z0-9_]+)\s*}}/g);
          if (matches) {
            matches.forEach(match => {
              const placeholder = match.replace(/{{\s*/, '').replace(/\s*}}/, '');
              textPlaceholders.add(placeholder);
            });
          }
        }
      });
    }

    // è™•ç†è¡¨æ ¼ä¸­çš„æ–‡å­—
    if (element.table && element.table.tableRows) {
      element.table.tableRows.forEach(row => {
        if (row.tableCells) {
          row.tableCells.forEach(cell => {
            if (cell.text && cell.text.textElements) {
              cell.text.textElements.forEach(textElement => {
                if (textElement.textRun && textElement.textRun.content) {
                  const content = textElement.textRun.content;
                  const matches = content.match(/{{\s*([A-Za-z0-9_]+)\s*}}/g);
                  if (matches) {
                    matches.forEach(match => {
                      const placeholder = match.replace(/{{\s*/, '').replace(/\s*}}/, '');
                      textPlaceholders.add(placeholder);
                    });
                  }
                }
              });
            }
          });
        }
      });
    }

    // è™•ç†åœ–ç‰‡å…ƒç´  - æª¢æŸ¥ description æˆ– title ä¸­çš„ placeholder
    if (element.image) {
      const description = element.description || '';
      const title = element.title || '';

      // æª¢æŸ¥ description ä¸­çš„ placeholder
      if (description.includes('{{') && description.includes('}}')) {
        const matches = description.match(/{{\s*([A-Za-z0-9_]+)\s*}}/g);
        if (matches) {
          matches.forEach(match => {
            const placeholder = match.replace(/{{\s*/, '').replace(/\s*}}/, '');
            imagePlaceholders.add(placeholder);
          });
        }
      }

      // æª¢æŸ¥ title ä¸­çš„ placeholder
      if (title.includes('{{') && title.includes('}}')) {
        const matches = title.match(/{{\s*([A-Za-z0-9_]+)\s*}}/g);
        if (matches) {
          matches.forEach(match => {
            const placeholder = match.replace(/{{\s*/, '').replace(/\s*}}/, '');
            imagePlaceholders.add(placeholder);
          });
        }
      }
    }
  });
}

// ğŸ§  åˆ†æ API è³‡æ–™çµæ§‹ï¼Œè‡ªå‹•åŒ¹é…æ¬„ä½
const allPlaceholders = new Set([...textPlaceholders, ...imagePlaceholders]);
const detectedFields = new Set();

if (slides.length > 0) {
  const sampleSlide = slides[0];
  Object.keys(sampleSlide).forEach(key => {
    detectedFields.add(key);
  });
}

// å»ºç«‹æŠ•å½±ç‰‡è¤‡è£½è«‹æ±‚
const totalSlides = slides.length;
const availableSlides = 1; // æ¨¡æ¿åªæœ‰ä¸€å¼µæŠ•å½±ç‰‡
const duplicateRequests = [];

if (totalSlides > availableSlides) {
  const duplicateCount = totalSlides - availableSlides;
  for (let i = 0; i < duplicateCount; i++) {
    duplicateRequests.push({
      duplicateObject: {
        objectId: templateSlide.objectId
        // ä¸æŒ‡å®šæ–° IDï¼Œè®“ Google Slides è‡ªå‹•ç”¢ç”Ÿ
      }
    });
  }
}

// æº–å‚™ä¸‹ä¸€éšæ®µéœ€è¦çš„è³‡æ–™
return {
  presentationId: presentationId,
  duplicateRequests: duplicateRequests,
  needsDuplication: duplicateRequests.length > 0,
  slidesData: slides,
  templateSlide: templateSlide,
  totalSlides: totalSlides,
  // ğŸ¯ æ–°å¢å‹•æ…‹åˆ†æçµæœ
  detectedTextPlaceholders: Array.from(textPlaceholders),
  detectedImagePlaceholders: Array.from(imagePlaceholders),
  detectedApiFields: Array.from(detectedFields),
  allPlaceholders: Array.from(allPlaceholders),
  mappingInfo: {
    templatePlaceholders: Array.from(allPlaceholders),
    apiFields: Array.from(detectedFields),
    unmatchedPlaceholders: Array.from(allPlaceholders).filter(p => !detectedFields.has(p)),
    unmatchedApiFields: Array.from(detectedFields).filter(f => !allPlaceholders.has(f))
  }
};

// ğŸš€ å‹•æ…‹æº–å‚™æ‰€æœ‰å…§å®¹æ›¿æ›è«‹æ±‚ - æ”¯æ´ä»»ä½•æ¬„ä½çµæ§‹

const prepData = $('Dynamic Template Analysis').first().json;
const updatedPresentation = $input.first().json;
const slides = prepData.slidesData;

// å–å¾—å‹•æ…‹åˆ†æçš„çµæœ
const textPlaceholders = prepData.detectedTextPlaceholders || [];
const imagePlaceholders = prepData.detectedImagePlaceholders || [];
const apiFields = prepData.detectedApiFields || [];

// å»ºç«‹æ‰€æœ‰å…§å®¹æ›¿æ›è«‹æ±‚
const allRequests = [];
const processLog = [];

// ğŸ§  æ™ºèƒ½æ¬„ä½åŒ¹é…å‡½å¼
function findBestMatch(placeholder, availableFields) {
  // 1. å®Œå…¨åŒ¹é…
  if (availableFields.includes(placeholder)) {
    return placeholder;
  }

  // 2. å¿½ç•¥å¤§å°å¯«åŒ¹é…
  const lowerPlaceholder = placeholder.toLowerCase();
  const exactMatch = availableFields.find(field => field.toLowerCase() === lowerPlaceholder);
  if (exactMatch) {
    return exactMatch;
  }

  // 3. éƒ¨åˆ†åŒ¹é…ï¼ˆåŒ…å«é—œä¿‚ï¼‰
  const partialMatch = availableFields.find(field => 
    field.toLowerCase().includes(lowerPlaceholder) || 
    lowerPlaceholder.includes(field.toLowerCase())
  );
  if (partialMatch) {
    return partialMatch;
  }

  // 4. èªç¾©åŒ¹é…ï¼ˆå¸¸è¦‹åˆ¥åï¼‰
  const synonymMap = {
    'title': ['main_title', 'heading', 'header', 'subject'],
    'subtitle': ['sub_title', 'subheading', 'description'],
    'content': ['text', 'body', 'detail'],
    'image': ['img', 'picture', 'photo', 'image_url'],
    'bullet1': ['point1', 'item1', 'first', 'bullet_1'],
    'bullet2': ['point2', 'item2', 'second', 'bullet_2'],
    'bullet3': ['point3', 'item3', 'third', 'bullet_3']
  };

  for (const [key, synonyms] of Object.entries(synonymMap)) {
    if (lowerPlaceholder.includes(key) || synonyms.some(syn => lowerPlaceholder.includes(syn))) {
      const match = availableFields.find(field => {
        const lowerField = field.toLowerCase();
        return lowerField.includes(key) || synonyms.some(syn => lowerField.includes(syn));
      });
      if (match) return match;
    }
  }

  return null;
}

// è™•ç†æ¯å¼µæŠ•å½±ç‰‡çš„å…§å®¹ï¼ˆä½¿ç”¨å¯¦éš›çš„æŠ•å½±ç‰‡ IDï¼‰
slides.forEach((slideDataItem, index) => {
  // å¾æ›´æ–°å¾Œçš„ç°¡å ±çµæ§‹ä¸­å–å¾—å¯¦éš›çš„æŠ•å½±ç‰‡ ID
  const currentSlide = updatedPresentation.slides[index];
  if (!currentSlide) return;

  const slideId = currentSlide.objectId;
  const slideLog = { slideIndex: index, slideId, textReplacements: [], imageReplacements: [] };

  // ğŸ”¤ å‹•æ…‹æ–‡å­—æ›¿æ›è«‹æ±‚
  textPlaceholders.forEach(placeholder => {
    const matchedField = findBestMatch(placeholder, Object.keys(slideDataItem));

    if (matchedField && slideDataItem[matchedField] !== undefined) {
      const value = slideDataItem[matchedField];

      // åªè™•ç†éç©ºå€¼
      if (value !== null && value !== undefined && value.toString().trim() !== '') {
        allRequests.push({
          replaceAllText: {
            containsText: {
              text: `{{${placeholder}}}`,
              matchCase: false
            },
            replaceText: value.toString().trim(),
            pageObjectIds: [slideId]
          }
        });

        slideLog.textReplacements.push({
          placeholder: `{{${placeholder}}}`,
          matchedField: matchedField,
          value: value.toString().trim()
        });
      }
    } else {
      // è¨˜éŒ„æœªåŒ¹é…çš„ placeholder
      slideLog.textReplacements.push({
        placeholder: `{{${placeholder}}}`,
        matchedField: null,
        status: 'no_match'
      });
    }
  });

  // ğŸ–¼ï¸ å‹•æ…‹åœ–ç‰‡æ›¿æ›è«‹æ±‚
  imagePlaceholders.forEach(placeholder => {
    const matchedField = findBestMatch(placeholder, Object.keys(slideDataItem));

    if (matchedField && slideDataItem[matchedField]) {
      const imageUrl = slideDataItem[matchedField];

      // é©—è­‰æ˜¯å¦ç‚ºæœ‰æ•ˆçš„ URL
      if (imageUrl && imageUrl.toString().trim() !== '' && 
          (imageUrl.toString().startsWith('http://') || imageUrl.toString().startsWith('https://'))) {

        // æ‰¾å‡ºè©²æŠ•å½±ç‰‡ä¸­ç¬¦åˆæ­¤ placeholder çš„åœ–ç‰‡å…ƒç´ 
        const imageElements = currentSlide.pageElements.filter(element => {
          if (!element.image) return false;

          // æª¢æŸ¥ description æˆ– title æ˜¯å¦åŒ…å«æ­¤ placeholder
          const description = element.description || '';
          const title = element.title || '';

          return description.includes(`{{${placeholder}}}`) || title.includes(`{{${placeholder}}}`);
        });

        imageElements.forEach(imageElement => {
          allRequests.push({
            replaceImage: {
              imageObjectId: imageElement.objectId,
              imageReplaceMethod: 'CENTER_CROP',
              url: imageUrl.toString()
            }
          });

          slideLog.imageReplacements.push({
            placeholder: `{{${placeholder}}}`,
            matchedField: matchedField,
            imageUrl: imageUrl.toString(),
            elementId: imageElement.objectId
          });
        });
      }
    } else {
      // è¨˜éŒ„æœªåŒ¹é…çš„åœ–ç‰‡ placeholder
      slideLog.imageReplacements.push({
        placeholder: `{{${placeholder}}}`,
        matchedField: null,
        status: 'no_match'
      });
    }
  });

  processLog.push(slideLog);
});

// ğŸ¯ è™•ç†å®Œå…¨å‹•æ…‹çš„æ¬„ä½ï¼ˆAPI ä¸­æœ‰ä½†æ¨¡æ¿ä¸­æ²’æœ‰ placeholder çš„ï¼‰
const unusedFields = apiFields.filter(field => 
  !textPlaceholders.some(p => findBestMatch(p, [field])) &&
  !imagePlaceholders.some(p => findBestMatch(p, [field]))
);

return {
  presentationId: prepData.presentationId,
  allRequests: allRequests,
  totalOperations: allRequests.length,
  textOperations: allRequests.filter(req => req.replaceAllText).length,
  imageOperations: allRequests.filter(req => req.replaceImage).length,
  slidesProcessed: slides.length,
  // ğŸ” è©³ç´°çš„è™•ç†è³‡è¨Š
  processingDetails: {
    templateAnalysis: {
      textPlaceholders: textPlaceholders,
      imagePlaceholders: imagePlaceholders,
      totalPlaceholders: textPlaceholders.length + imagePlaceholders.length
    },
    apiAnalysis: {
      detectedFields: apiFields,
      unusedFields: unusedFields
    },
    matchingResults: processLog,
    summary: {
      successfulTextMatches: processLog.reduce((sum, log) => 
        sum + log.textReplacements.filter(r => r.status !== 'no_match').length, 0),
      successfulImageMatches: processLog.reduce((sum, log) => 
        sum + log.imageReplacements.filter(r => r.status !== 'no_match').length, 0),
      unmatchedTextPlaceholders: processLog.reduce((sum, log) => 
        sum + log.textReplacements.filter(r => r.status === 'no_match').length, 0),
      unmatchedImagePlaceholders: processLog.reduce((sum, log) => 
        sum + log.imageReplacements.filter(r => r.status === 'no_match').length, 0)
    }
  }
};

